import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EventDiscovery = () => {
    const [events, setEvents] = useState([]);
    const [search, setSearch] = useState('');

    useEffect(() => {
        // This reaches out to your backend server
        axios.get(`http://localhost:5000/api/events?search=${search}`)
            .then(res => setEvents(res.data))
            .catch(err => console.log("Make sure your server is running!", err));
    }, [search]);

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial' }}>
            <h2>Available Events</h2>
            <input 
                type="text" 
                placeholder="Search by name..." 
                style={{ padding: '10px', marginBottom: '20px', width: '300px' }}
                onChange={(e) => setSearch(e.target.value)}
            />
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '20px' }}>
                {events.map(event => (
                    <div key={event._id} style={{ border: '1px solid #ddd', padding: '15px', borderRadius: '8px', boxShadow: '2px 2px 10px rgba(0,0,0,0.1)' }}>
                        <h3 style={{ color: '#007bff' }}>{event.name}</h3>
                        <p><strong>üìç Location:</strong> {event.location}</p>
                        <p><strong>üè∑Ô∏è Category:</strong> {event.category}</p>
                        <button style={{ background: '#28a745', color: 'white', border: 'none', padding: '10px', borderRadius: '5px', cursor: 'pointer' }}>
                            Register Now
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default EventDiscovery;
